import { AZURE_FUNCTIONS } from '../config/config';

/**
 * Envoie un email de bienvenue via l'Azure Function
 * N'échoue pas le flux d'inscription si l'appel échoue.
 */
export async function sendWelcomeEmail(email, name) {
  try {
    const response = await fetch(AZURE_FUNCTIONS.WELCOME_EMAIL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, name }),
    });

    if (!response.ok) {
      // Log, mais ne bloque pas
      console.warn('Azure Function email non-OK:', response.status);
      return false;
    }

    return true;
  } catch (err) {
    console.warn('Erreur envoi email Azure Function:', err);
    return false;
  }
}

export default { sendWelcomeEmail };
